  private static void parse(JSONObject j)
  {
    if (j.containsKey("x") && j.containsKey("y") && j.containsKey("z"))
    {
      Object x = j.get("x");
      Object y = j.get("y");
      Object z = j.get("z");
      j.clear();
      j.put("x", x);
      j.put("y", y);
      j.put("z", z);
    }
    Collection<Object> cs = j.values();
    for (Object s : cs)
    {
      if (s instanceof JSONObject)
      {
        parse((JSONObject) s);
      } else if (s instanceof JSONArray)
      {
        JSONArray arr = (JSONArray) s;
        Iterator<Object> it = arr.iterator();
        while (it.hasNext())
        {
          Object i = it.next();
          if (i instanceof JSONObject)
          {
            parse((JSONObject) i);
          }
        }
      }
    }
  }
